import React, { useState, useRef } from 'react';
import { uploadResume } from '../services/api';
import '../styles/UploadForm.css';


const UploadForm = ({ setSkills, setLoading, setError, setFileName }) => {
  const [isDragging, setIsDragging] = useState(false);
  const [file, setFile] = useState(null);
  const fileInputRef = useRef(null);

  /**
   * Handle file selection
   * @param {Event} e - File input change event
   */
  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile && selectedFile.type === 'application/pdf') {
      setFile(selectedFile);
    } else {
      alert('Please select a valid PDF file.');
    }
  };

  /**
   * Handle file submission
   * @param {Event} e - Form submit event
   */
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!file) {
      alert('Please select a PDF file before submitting.');
      return;
    }

    setFileName(file.name);
    setLoading(true);
    setError(null);
    setSkills(null);

    try {
      const response = await uploadResume(file);
      setSkills(response.skills);
    } catch (err) {
      console.error('Error uploading file:', err);
      setError(err.message || 'An error occurred while processing your resume.');
    } finally {
      setLoading(false);
    }
  };

  /**
   * Handle click on the upload area
   */
  const handleUploadClick = () => {
    fileInputRef.current.click();
  };

  /**
   * Handle drag over event
   * @param {Event} e - Drag event
   */
  const handleDragOver = (e) => {
    e.preventDefault();
    setIsDragging(true);
  };

  /**
   * Handle drag leave event
   * @param {Event} e - Drag event
   */
  const handleDragLeave = (e) => {
    e.preventDefault();
    setIsDragging(false);
  };

  /**
   * Handle drop event
   * @param {Event} e - Drop event
   */
  const handleDrop = (e) => {
    e.preventDefault();
    setIsDragging(false);

    const droppedFile = e.dataTransfer.files[0];
    if (droppedFile && droppedFile.type === 'application/pdf') {
      setFile(droppedFile);
    } else {
      alert('Please drop a valid PDF file.');
    }
  };

  return (
    <div className="text-center">
      <div 
        className={`upload-area mb-4 ${isDragging ? 'dragging' : ''}`}
        onClick={handleUploadClick}
        onDragOver={handleDragOver}
        onDragLeave={handleDragLeave}
        onDrop={handleDrop}
      >
        <div className="upload-icon mb-3">
          <i className="fas fa-file-pdf fa-3x"></i>
        </div>
        <h4>Drag and drop your resume</h4>
        <p className="text-muted">or click to select a PDF file</p>

        <input 
          type="file" 
          className="d-none" 
          ref={fileInputRef}
          onChange={handleFileChange}
          accept=".pdf"
        />

        {file && (
          <div className="selected-file mt-3">
            <div className="alert alert-info d-inline-block">
              <i className="fas fa-check-circle me-2"></i>
              {file.name}
            </div>
          </div>
        )}
      </div>

      <button 
        className="btn btn-primary btn-lg"
        onClick={handleSubmit}
        disabled={!file}
      >
        <i className="fas fa-magic me-2"></i> Extract Skills
      </button>

      <div className="mt-4">
        <p className="text-muted small">
          Supported format: PDF only. Maximum size: 10MB.
        </p>
      </div>
    </div>
  );
};

export default UploadForm;
